import{_ as l,a as s,b as n,c as a,d as i}from"./TestNavigationButtons-2fed292e.js";import{_ as b}from"./QuestionTemp4.vue_vue_type_script_setup_true_lang-d1f40b41.js";import{_ as u}from"./SeihoTestLayout-cd119090.js";import{o as c,c as p,w as m,b as o,a as t}from"./app-e328c2ef.js";import"./AdSenseUnit.vue_vue_type_script_setup_true_lang-6541f7f5.js";const g={class:"bg-gray-100 text-gray-800 py-10"},_={class:"max-w-4xl mx-auto mt-4 space-y-2"},e="生命保険計理",r="2023年度 フォームB",$={__name:"Keiri2023b",setup(x){return(w,f)=>(c(),p(u,{title:e},{default:m(()=>[o("section",g,[t(l,{title:e,description:r}),o("div",_,[t(b,{questionNumber:1,title:e,subject:r,contents:[{title:"生存率・生存数・平均余命・保険料計算",answer:"0.97957",explanation:"<br>                        ＝ 93,054 ÷ 94,995<br>                        ＝ 0.979567･･･<br>                        ⇒ 小数第6位を四捨五入して <strong>0.97957</strong>"},{title:"生存率・生存数・平均余命・保険料計算",answer:"98,756",explanation:"<br>生存数 ＝ 期末の生存数 ÷ 期初の生存数 × 期初の生存者数<br><br>                        ＝ 97,539 ÷ 98,768 × 100,000<br>                        ＝ 98755.6･･･<br>                        ⇒ 小数第1位を四捨五入して <strong>98,756</strong>"},{title:"生存率・生存数・平均余命・保険料計算",answer:"1.02",explanation:"<br>死亡は年間を通じて発生するが、平均して年央に集中発生したものと考えられるので、例えば、100歳から101歳になるまでに死亡する人の平均生存年数は0.5年となる。<br><br>                        年齢 ： 死亡数 × 平均生存年数 ＝ 延べ生存年数<br>                        109歳 ： 25.888人 × 0.5年 ＝ 12.944 ＝ 12.94<br>                        110歳 ： 10.1008人 × 1.5年 ＝ 15.1512 ＝ 15.15<br>                        111歳 ： 3.4515人 × 2.5年 ＝ 8.62875 ＝ 8.63<br>                        112歳 ： 1.0162人 × 3.5年 ＝ 3.5567 ＝ 3.56<br>                        113歳 ： 0.3155人 × 4.5年 ＝ 1.41975 ＝ 1.42<br>                        → 109歳～113歳の延べ生存年数の合計 ＝ 45.15<br><br>                        平均余命 ＝ 延べ生存年数の合計 ÷ 生存者数<br><br>                        ＝ 41.7 ÷ 40.772 ＝ 1.022･･･<br>                        ⇒ 小数第3位を四捨五入して <strong>1.02</strong>"},{title:"生存率・生存数・平均余命・保険料計算",answer:"776,129",explanation:"<br>55歳の女性は1年間に260人が死亡し、1人あたりの保険金は300万円なので、合計の保険金額は<br>                        300万円 × 260人 ＝ 780,000千円<br><br>                        支払いは年央と仮定し、利率1.0%の期央払現価率0.995037で割引くと<br>                        780,000千円 × 0.995037 ＝ 776,128.86千円<br>                        ⇒ 千円未満を四捨五入して <strong>776,129千円</strong>"},{title:"生存率・生存数・平均余命・保険料計算",answer:"8,050",explanation:"<br>問4で求めた死亡保険金の支払いに必要な費用は、55歳女性の年始生存者数96,441人で平等に分担するので、<br>                        776,129千円 ÷ 96,441人 ＝ 8,047.70793円<br>                        ⇒ 十円未満を四捨五入して <strong>8,050円</strong>"}],labels:["イ","キ","ウ","コ","カ"]}),t(b,{questionNumber:6,title:e,subject:r,contents:[{title:"複利運用",answer:"6,015",explanation:"<br>                        1,000,000円 × (1 + 0.001) <sup>6</sup> ＝ 1,006,015.02<br><br>                        元金との差額が利息なので、<br>                        1,006,015.02 - 1,000,000 ＝ 6,015.02<br>                        ⇒ 円未満を四捨五入して <strong>6,015円</strong>"},{title:"複利運用",answer:"1,061,646",explanation:"<br>年利1.2%で半年複利のため、利率は半年あたり0.6%（=0.006）となり、5年間で10回の複利が適用される。<br><br>                        よって元金1,000,000円の場合の元利合計金額は、<br>                        (1 + 0.006)<sup>10</sup> ＝ 1.0616461･･･ ＝ 1.061646(※)より<br>                        ※小数第7位四捨五入<br><br>                        1,000,000 × 1.061646 ＝ 1,061,646･･･<br>                        ⇒ 円未満を四捨五入して <strong>1,061,646円</strong>"},{title:"複利運用",answer:"1.51",explanation:"<br>転化回数：1年間に利息を元金に繰り入れる回数<br><br><br>                        実利率 = （ 1 + 名称利率 ÷ 転化回数）<sup>転化回数</sup> - 1<br><br>                        （ 1 + 0.015 ÷ 4 ）<sup>4</sup> - 1 ＝ 0.015084 ＝ 1.5084%<br>                        ⇒ 小数第3位を四捨五入して <strong>1.51%</strong>"},{title:"複利運用",answer:"1.33",explanation:"<br>年平均利回り：投資によって得られる利益を、投資期間全体で年単位に平均化したもの<br><br>                        年平均利回り ＝ （（ 1 + 複利利率）<sup>期間</sup> - 1 ） ÷ 期間<br><br><br>                        （（ 1 + 0.013 ）<sup>5</sup> - 1 ） ÷ 5 ＝ 0.0133424 ＝ 1.334･･･%<br>                        ⇒ 小数第3位を四捨五入して <strong>1.33%</strong>"},{title:"複利運用",answer:"1,941,180",explanation:"<br>元金 × （ 1 + 利率 ）<sup>期間</sup> ＝ 元利合計<br>                        元金 × （ 1 + 1.0% ）<sup>3年</sup> ＝ 200万円<br>                        元金 ＝ 2,000,000 ÷ （ 1 + 0.01 ）<sup>3</sup> ＝ 1,941,180.295<br>                        ⇒ 円未満を四捨五入して <strong>1,941,180円</strong>"}],labels:["カ","ク","ウ","イ","コ"]},null,8,["contents"]),t(s,{questionNumber:3,questionTitle:"死亡表の種類",title:e,subject:r,contents:["経験生命表","完全生命表","総合表","選択効果","年齢別"],labels:["ウ","ア","キ","カ","コ"]}),t(s,{questionNumber:4,questionTitle:"払済保険",title:e,subject:r,contents:["解約返戻金","残存","そのときの被保険者の到達年齢","新契約費","同じ"],labels:["ウ","キ","ケ","ク","イ"]}),t(b,{questionNumber:21,title:e,subject:r,contents:[{title:"死亡保険金の現価について",answer:"2,139,522千円",explanation:"<br>                        死亡保険金 × 死亡数 × 期央払現価 ＝ 死亡保険金の現価<br>                        50歳：2,000,000 × 192 × 0.995037 ＝ 382,094千円<br>                        51歳：2,000,000 × 205 × 0.985185 ＝ 403,926千円<br>                        52歳：2,000,000 × 219 × 0.975431 ＝ 427,239千円<br>                        53歳：2,000,000 × 234 × 0.965773 ＝ 451,982千円<br>                        54歳：2,000,000 × 248 × 0.956211 ＝ 474,281千円<br>                        ⇒ 合計：<strong>2,139,522千円</strong>"},{title:"満期保険金の現価について",answer:"183,520,665千円",explanation:"<br>5年後に支払う満期保険金の現価は、<br>                        5年後の生存数 × 保険金額 × 期末払現価で求める。<br><br>                        55歳の生存数 × 200万円 × 5年の期末払現価<br>                        96,441 × 2,000,000 × 0.951466 ＝ 183,520,665,012円<br>                        ⇒ 千円未満を四捨五入して <strong>183,520,665千円</strong>"},{title:"予定事業費の現価について",answer:"4,876,950千円",explanation:"<br>予定事業費は保険金1000円につき25円より、一人当たりの予定事業費に加入時の生存数をかけて求める。<br><br><br>                        保険金額 × 予定事業費率 × 50歳の生存数<br>                        2,000,000 × (25 ÷ 1000) × 97,539 ＝ 4,876,950,000<br>                        ⇒ 千円未満を四捨五入して <strong>4,876,950千円</strong>"},{title:"養老保険の年払営業保険料について",answer:"400,170円",explanation:"<br>収支相当の原則「収入現価 ＝ 支出現価」で求める。<br><br>                        ＜収入現価＞<br>                        収入の現価は、50歳〜54歳の各年齢の生存数×年払保険料（P）を現価に換算して合計する。<br><br><br>                        年払保険料 × 生存数 × 期始払現価 ＝ 収入現価<br>                        50歳 ： P × 97,539 × 1.000000 ＝ 97,539P<br>                        51歳 ： P × 97,347 × 0.990099 ＝ 96,383P<br>                        52歳 ： P × 97,141 × 0.980296 ＝ 95,227P<br>                        53歳 ： P × 96,923 × 0.970590 ＝ 94,072P<br>                        54歳 ： P × 96,689 × 0.960980 ＝ 92,916P<br>                        → 50歳～54歳の収入現価の合計 ＝ 476,137P<br><br>                        ＜支出現価＞<br>                        支出現価は問21〜23で算出した結果を用いて求める。<br><br><br>                        死亡保険金の現価+満期保険金の現価+予定事業費の現価<br>                        ＝ 2,139,522千円 + 183,520,665千円 + 4,876,950千円<br>                        ＝ 190,537,137千円<br><br>                        収支相当の原則より<br>                        476,137P = 190,537,137千円<br>                        P ＝ 400,172･･･円 <br>                        ⇒ 10円未満を四捨五入して <strong>400,170円</strong>"}],labels:["ア","イ","ウ","イ"]}),t(n,{questionNumber:25,title:e,subject:r,contents:["配当金を<u><b>一時</b></u>払保険料として契約応当日に保険を買増し（保険金を増額）する。","配当金を保険料払込回数に等分して、毎回の保険料に充当、相殺する。","正しい"]},null,8,["contents"]),t(n,{questionNumber:26,title:e,subject:r,contents:["保険金を比例させる方式、営業保険料（または純保険料）に比例させる方式、<u>1契約について予定事業費を一定額とする方式</u>、これらの方式を組み合わせる方式の4方式のいずれかである。","正しい","正しい"]},null,8,["contents"]),t(n,{questionNumber:27,title:e,subject:r,contents:["正しい","正しい","保険計理人は、責任準備金が健全な保険計理に基づいて積み立てられているかどうかを確認し、その結果を配当の確認結果とあわせて意見書に記載して、<u><b>取締役会</b></u>に提出することが保険業法に定められている。"]}),t(n,{questionNumber:28,title:e,subject:r,contents:["延長保険は、払済保険とは対照的に死亡保障に重点を置いて死亡保険金額は変わらないが、保険期間が短くなることもあり、満期時に受け取る保険金も<u><b>ゼロまたは少額</b></u>になる。","正しい","正しい"]}),t(n,{questionNumber:29,title:e,subject:r,contents:["正しい","解約返戻金は、保険料の自動振替貸付と契約者貸付の限度基準として用いられている。","正しい"]},null,8,["contents"]),t(n,{questionNumber:30,title:e,subject:r,contents:["正しい","配当金の支払いを行わない無配当保険や5年ごと利差配当付保険のような保険は、保険料は通常の有配当保険より<u><b>安くなる</b></u>のが一般的である。","正しい"]},null,8,["contents"]),t(a,{questionNumber:31,title:e,subject:r,items:[{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"生命保険会社では、満x歳を中心に前後6カ月の被保険者に対してx歳の保険料率を適用する「保険年齢方式」を採用している会社と、通常の年齢の数え方と同じ切捨方式（端数月はすべて切り捨てる）である「満年齢方式」を採用している会社がある。"},{label:"解",content:"正しい"},{label:"解",content:"自然保険料ではなく平準保険料の説明。<br>毎年の保険料収入と保険金支出とが相等しくなるように定めた保険料を自然保険料という。"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"変額保険の変動保険金額は、毎月<u><b>月末</b></u>に、その時点での積立金と表定責任準備金との差額と、当該<u><b>月末</b></u>時点で計算された一時払保険料を用いて計算される。"},{label:"解",content:"正しい"},{label:"オ",content:""},{label:"イ",content:"大数"},{label:"エ",content:"高くなる"},{label:"ウ",content:"平準保険料"},{label:"ア",content:"危険保険金"},{label:"イ",content:"標準"},{label:"オ",content:""},{label:"オ",content:""},{label:"ア",content:"毎月"},{label:"ウ",content:"通常保険料"}]})]),t(i,{"previous-route":"keiri2023a","next-route":"keiri2023c"})])]),_:1}))}};export{$ as default};
