import{_ as l,a as s,b as e,c as u,d as o}from"./TestNavigationButtons-dd051d1d.js";import{_ as n}from"./QuestionTemp4.vue_vue_type_script_setup_true_lang-245a3630.js";import{_ as i}from"./SeihoTestLayout-203107a5.js";import{o as c,c as p,w as m,b as a,a as b}from"./app-8b98acf3.js";import"./AdSenseUnit.vue_vue_type_script_setup_true_lang-2891d741.js";const _={class:"bg-gray-100 text-gray-800 py-10"},x={class:"max-w-4xl mx-auto mt-4 space-y-2"},r="生命保険計理",t="2024年度 フォームC",h={__name:"Keiri2024c",setup(w){return(f,N)=>(c(),p(i,{title:r},{default:m(()=>[a("section",_,[b(l,{title:r,description:t}),a("div",x,[b(n,{questionNumber:1,title:r,subject:t,contents:[{answer:"0.98925",explanation:"生存率 ＝ 期末の生存数 ÷ 期初の生存数<br><br>                        生存率：<br>                        96,319 ÷ 97,366 ＝ 0.989246<br>                        ⇒ 0.98925（小数第6位を四捨五入）"},{answer:"93,714",explanation:"生存数 ＝ 期末の生存数 ÷ 期初の生存数 × 期初の生存者数<br><br><br>                        生存数：<br>                        88,751 ÷ 94,704 × 100,000 ＝ 93714.0<br>                        ⇒93,714（小数第1位を四捨五入）"},{answer:"1.16",explanation:"前提：<br>                        死亡は年央に集中して発生するものと仮定。<br>                        各年齢での平均生存年数を元に延べ生存年数を求める。<br><br><br>                        年齢 ： 死亡数 × 平均生存年数 ＝ 延べ生存年数<br>                        105歳 ： 30.877人 × 0.5年 ＝ 15.44<br>                        116歳 ： 13.8961人 × 1.5年 ＝ 20.84<br>                        107歳 ： 5.6633人 × 2.5年 ＝ 14.16<br>                        108歳 ： 2.0682人 × 3.5年 ＝ 7.24<br>                        109歳 ： 0.9156人 × 4.5年 ＝ 4.12<br>                        105歳～109歳の延べ生存年数の合計 ＝ 61.8<br><br><br>                        平均余命 ＝ 延べ生存年数の合計 ÷ 生存者数<br><br><br>                        平均余命：<br>                        61.8 ÷ 53.420 ＝ 1.156<br>                        ⇒ 1.16（小数第3位を四捨五入）"},{answer:"1,368,176",explanation:"50歳男性の死亡数：275人<br>                        1人あたりの保険金：500万円<br><br><br>                        合計保険金額 ＝ 500万円 × 275人 ＝ 1,375,000千円<br><br><br>                        支払いは年央と仮定<br>                        利率1.0%の期央払現価率：0.995037<br><br><br>                        現価 ＝ 1,375,000千円 × 0.995037 ＝ 1,368,175.8千円<br><br><br>                        必要な現価：<br>                        ⇒ 1,368,176千円（千円未満を四捨五入）"},{title:"生存率・生存数・平均余命・保険料計算",answer:"14,200",explanation:"必要な現価：1,368,176千円（問4より）<br>                        50歳男性の年始生存者数：96,319人<br><br><br>                        保険料 ＝ 1,368,176千円 ÷ 96,319人 ＝ 14,204円<br><br><br>                        1人あたりの保険料：<br>                        ⇒ 14,200円（十円未満を四捨五入）"}],labels:["イ","ク","ウ","コ","オ"]}),b(n,{questionNumber:6,title:r,subject:t,contents:[{title:"複利運用",answer:"4,517",explanation:"元金：500,000円<br>                        月利：0.15%（＝0.0015）<br><br><br>                        1ヶ月複利で6ヶ月間運用した場合の元利合計金額を求める。<br><br><br>                        500,000円 × (1 + 0.0015) <sup>6</sup> ＝ 504,516.9円<br><br><br>                        利息：<br>                        504,516.9円 − 500,000円 ＝ 4,516.9円<br>                        ⇒ 4,517円（円未満を四捨五入）"},{title:"複利運用",answer:"528,696",explanation:"元金：500,000円<br>                        年利：1.4%<br>                        半年利：0.7%（＝0.007）<br>                        複利回数：8回（＝4年間 × 年2回）<br><br><br>                        500,000円 × (1 + 0.007)<sup>8</sup> ＝ 528,695.6円<br><br><br>                        元利合計：<br>                        ⇒ 528,696円（円未満を四捨五入）"},{title:"複利運用",answer:"1.21",explanation:"転化回数：1年間に利息を元金に繰り入れる回数<br><br><br>                        実利率 ＝（ 1 + 名称利率 ÷ 転化回数）<sup>転化回数</sup> - 1<br><br><br>                        実利率：<br>                        （ 1 + 0.012 ÷ 4 ）<sup>4</sup> - 1 ＝ 0.01205 ＝ 1.205%<br>                        ⇒ 1.21%（小数第3位を四捨五入）"},{title:"複利運用",answer:"1.83",explanation:"複利利率：1.8%（＝0.018）<br>                        投資期間：3年<br>                        年平均利回り ＝ （（ 1 + 複利利率）<sup>期間</sup> - 1 ） ÷ 期間<br><br><br>                        年平均利回り ＝ ((1 + 0.018)<sup>3</sup> − 1) ÷ 3 ＝ 0.01832（＝1.832%）<br><br><br>                        年平均利回り：<br>                        ⇒ 1.83%（小数第3位を四捨五入）"},{title:"複利運用",answer:"4,618,507",explanation:"年利率：1.6%（＝0.016）<br>                        投資期間：5年<br>                        元利合計 ＝ 元金 ×（ 1 + 利率 ）<sup>期間</sup><br><br><br>                        元金 ×（ 1 + 1.6% ）<sup>5年</sup> ＝ 500万円<br>                        元金 ＝ 5,000,000 ÷ （ 1 + 0.016 ）<sup>5</sup> ＝ 4,618,505.4<br>                        ⇒ 4,618,505円（円未満を四捨五入）<br><br><br>                        ※本解説は標準的な複利計算に基づいていますが、模範解答と結果が相違しております。<br>                        出題側の処理基準が不明確なため、本解説の方が正確である可能性もあります。"}],labels:["オ","ク","ア","ウ","コ"]}),b(s,{questionNumber:3,title:r,subject:t,contents:["純保険料","全払込期間","定期保険","後半","ゼロ"],labels:["オ","キ","ケ","ウ","ア"]}),b(s,{questionNumber:4,title:r,subject:t,contents:["将来法","一致する","貯蓄保険料","付加保険料","チルメル"],labels:["キ","ウ","ク","コ","ケ"]}),b(n,{questionNumber:21,title:r,subject:t,contents:[{answer:"6,405,460千円",explanation:"保険金額：500万円<br>                        保険期間：3年（55歳〜57歳）<br><br>                        死亡保険金の支払いは平均して年の中央で発生すると仮定<br>                        各年齢における死亡保険金の現価求めて合計する<br><br><br>                        死亡保険金 × 死亡数 × 期央払現価 ＝ 死亡保険金の現価<br>                        55歳：5,000,000 × 400 × 0.995037 ＝ 1,990,074千円<br>                        56歳：5,000,000 × 432 × 0.985185 ＝ 2,128,000千円<br>                        57歳：5,000,000 × 469 × 0.975431 ＝ 2,287,386千円<br>                        死亡保険金現価の合計：<br>                        ⇒ 6,405,460千円"},{answer:"453,280,089千円",explanation:"保険金額：500万円<br>                        満期保険金の現価：3年後の生存数 × 保険金額 × 期末払現価<br><br>                        58歳の生存数 × 500万円 × 3年の期末払現価<br>                        93,403 × 5,000,000 × 0.970590 ＝ 453,280,088,850円<br><br>                        3年後に支払う満期保険金の現価：<br>                        ⇒ 453,280,089千円（千円未満を四捨五入）"},{answer:"11,838,000千円",explanation:"保険金額：500万円<br>                        予定事業費：保険金1000円につき25円<br><br><br>                        一人当たりの予定事業費に加入時の生存数をかけて求める。<br><br><br>                        保険金額 × 予定事業費率 × 55歳の生存数<br>                        5,000,000 × (25 ÷ 1000) × 94,704 ＝ 11,838,000,000<br><br>                        予定事業費の現価：<br>                        ⇒ 11,838,000千円（千円未満を四捨五入）"},{answer:"1,683,420円",explanation:"収支相当の原則：収入現価 ＝ 支出現価<br><br><br>                        ＜収入現価＞<br>                        各年齢の生存数×年払保険料（P）を現価に換算して合計する。<br><br><br>                        年払保険料 × 生存数 × 期始払現価 ＝ 収入現価<br>                        55歳 ： P × 94,704 × 1.000000 ＝ 94,704P<br>                        56歳 ： P × 94,305 × 0.990099 ＝ 93,371P<br>                        57歳 ： P × 93,873 × 0.980296 ＝ 92,023P<br>                        55歳～57歳の収入現価の合計 ＝ 280,098P<br><br>                        ＜支出現価＞<br>                        問21〜23で算出した結果を用いて求める。<br><br><br>                        死亡保険金の現価+満期保険金の現価+予定事業費の現価<br>                        ＝ 6,405,460千円 + 453,280,089千円 + 11,838,000千円<br>                        ＝ 471,523,549千円<br><br>                        収支相当の原則より<br>                        280,098P = 471,523,549千円<br>                        P ＝ 1,683,423円 <br><br>                        営業保険料：<br>                        ⇒ 1,683,420円（10円未満を四捨五入）"},{answer:"1,097,000円",explanation:"払済保険金 ＝解約返戻金 × (保険金額 ÷ 養老保険の一時払保険料)<br><br><br>                        858,000 × (3,000,000 ÷ 2,345,580) ＝ 1,097,383円<br><br><br>                        払済保険金：<br>                        ⇒ 1,097,000円（千円未満を四捨五入）"}],labels:["ア","イ","ウ","イ","ア"]}),b(e,{questionNumber:26,title:r,subject:t,contents:["正しい","簡易生命表ではなく完全生命表。<br>簡易生命表は、毎年の人口動態統計の死亡数と推計人口を用い、簡略化された計算によって算定された死亡率を表示したものである。","正しい"]}),b(e,{questionNumber:27,title:r,subject:t,contents:["正しい","利差益は責任準備金に<u>比例</u>するので、貯蓄性の強い保険、養老保険などについては、契約初期は<u><b>少なく</b></u>、経過年数が長くなるほど<u>多く</u>なる。","正しい"]}),b(e,{questionNumber:28,title:r,subject:t,contents:["保険業法では、事業年度末までに経過した期間に見合う保険料部分に対応する積立金（保険料積立金）、翌事業年度以降に対応する保険料部分（未経過保険料）、<u>第三分野における積立傷害保険等の積立型商品において、将来の満期返戻金支払いに対応する保険料部分（払戻積立金）</u>および危険準備金に分けて計算することとされている。","正しい","正しい"]},null,8,["contents"]),b(e,{questionNumber:29,title:r,subject:t,contents:["正しい","正しい","転換前契約の責任準備金は転換後契約の一時払保険料（新契約費部分を<u><b>含まない</b></u>もの）に充当する。"]},null,8,["contents"]),b(e,{questionNumber:30,title:r,subject:t,contents:["年金制度の財政方式のうち代表的な事前積立方式は、<u><b>一時払積増方式</b></u>、<u><b>平準積立方式</b></u>である。","正しい","正しい"]}),b(u,{questionNumber:31,title:r,subject:t,items:[{label:"解",content:"実際の人口統計に基づく死亡率を<u><b>粗死亡率</b></u>と呼ぶのに対し、数学的な技術を使って滑らかな傾向曲線にして求められた死亡率を<u><b>補整後死亡率</b></u>と呼んで区別している。"},{label:"解",content:"通常、単に現価という場合には<u><b>期末払現価</b></u>を指す。"},{label:"解",content:"正しい"},{label:"解",content:"1年分を何回かに分けて払い込む分割払営業保険料の年間払込額は、年払の営業保険料額を以下の理由で<u><b>上回る</b></u>。<br>①分割払のために保険料払込みが遅れるための利息の損失（年払保険料の場合に比べて年間の予定利息分は少なくなる）<br>②集金回数（払込回数）の増加に伴う事業費の増加"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"5年ごと利差配当付保険の場合、次の事業年度中に死亡、解約等により消滅する契約について、直前の5年ごとの契約応当日から<u><b>一定年数経過している</b></u>場合に配当が割り当てられる。"},{label:"解",content:"正しい"},{label:"解",content:"一般的な変額個人年金保険に付されている最低保証機能については、「最低年金原資保証タイプ」、「最低死亡保証タイプ」、<u><b>「最低解約保証タイプ」</b></u>、「最低年金額保証タイプ」の4つのタイプに大別することができる。"},{label:"オ",content:""},{label:"ア",content:"現価"},{label:"ウ",content:"保険金に比例させる"},{label:"エ",content:"（1 + 予定利率）"},{label:"ア",content:"養老保険"},{label:"ア",content:"定款"},{label:"オ",content:""},{label:"オ",content:""},{label:"エ",content:"を禁止されている"},{label:"オ",content:""}]})]),b(o,{"previous-route":"keiri2024b","next-route":"keiri2023a"})])]),_:1}))}};export{h as default};
