import{_ as o,a as b,b as s,c,d as l}from"./TestNavigationButtons-eafc327e.js";import{S as a}from"./SeihoTestLayout-c1798e3b.js";import{o as i,c as r,w as m,b as u,a as t}from"./app-1e26cb44.js";import"./AdSenseUnit.vue_vue_type_script_setup_true_lang-dc171581.js";const _={class:"bg-gray-100 text-gray-800 py-10"},N={class:"max-w-4xl mx-auto mt-4 space-y-2"},e="危険選択",n="2024年度 フォームC",h={__name:"Kiken2024c",setup(j){return(q,p)=>(i(),r(a,{title:e},{default:m(()=>[u("section",_,[t(o,{title:e,description:n}),u("div",N,[t(b,{questionNumber:1,title:e,subject:n,contents:["故意","民法","必要はない","解約返戻金","到達"],labels:["オ","キ","ウ","ク","カ"]}),t(b,{questionNumber:2,title:e,subject:n,contents:["しない","因果関係","5年","責任開始期の属する日","2年"],labels:["ケ","カ","ウ","キ","ア"]}),t(b,{questionNumber:3,title:e,subject:n,contents:["収縮期","拡張期","本態性高血圧","先天性","インシュリン"],labels:["キ","カ","ア","オ","コ"]}),t(b,{questionNumber:4,title:e,subject:n,contents:["赤血球","肝硬変","肝臓がん","ストレス","リンパ球"],labels:["ア","カ","コ","ウ","オ"]}),t(s,{questionNumber:21,title:e,subject:n,contents:["「実体的危険」は、<u><b>「身体的危険」</b></u>と「環境的危険」の2つに分類することができる。","環境的危険とは、被保険者の生活環境全般に関する危険であるが主に被保険者の職業・仕事の内容による危険を指す","正しい"]}),t(s,{questionNumber:22,title:e,subject:n,contents:["診査による選択有効期間は3～5年と考えられているが、<u><b>国民生命表との比較では、かなり長期に及ぶことが明らかになっている</b></u>。","正しい","<u><b>若年齢層</b></u>は健康体も多く、安い保険料で加入することができるが、その死因では、不慮の事故が上位を占めているので、<u><b>診査の効果は顕著ではない</b></u>。他方、<u><b>高年齢層</b></u>では、保険料は高いが、診査によって契約を延期されたり、条件体になる率も高い。しかしながら、診査自体に加えて心電図・血液検査などの補助診断法を併用することによって<u><b>選択効果を期待することができる</b></u>。"]}),t(s,{questionNumber:23,title:e,subject:n,contents:["大部分の保険会社は条件体契約に対して、年齢別に金額上の区分を設けて制限を加えている。","「保険会社向けの総合的な監督指針」では、法人等の財テクなどを主たる目的とした契約または当初から短期の中途解約を前提とした契約等の保険本来の趣旨を逸脱するような募集活動を行わないことなど、保険商品のそれぞれの商品特性に応じ，その本来の目的に沿った利用が行われるための適切な募集活動に対する措置を求めている。","正しい"]}),t(s,{questionNumber:24,title:e,subject:n,contents:["欧米では社医制度はなく、一般に嘱託医だけである。","正しい","その保険契約が被保険者で一定数（一般には20名）以上の申込みがある団体扱であること、被保険者個々の告知書や健康管理証明書の提出を必要としないことが「健康管理証明書扱（個別）」と異なる点である。"]}),t(s,{questionNumber:25,title:e,subject:n,contents:["正しい","危険保険料式再保険の説明。","共同保険式再保険の説明。"]}),t(s,{questionNumber:26,title:e,subject:n,contents:["正しい","定期保険の責任準備金の積み立ての説明。","正しい"]}),t(s,{questionNumber:27,title:e,subject:n,contents:["死亡率や<u><b>入院給付率</b></u>は、保険年度が進み選択効果が薄れるにつれ上昇するが、<u><b>手術給付率</b></u>はむしろ加入早期の方が高値である。","正しい","正しい"]}),t(s,{questionNumber:28,title:e,subject:n,contents:["正しい","正しい","申込保険金額(災害・傷害・災害入院、疾病入院などの諸特約金額も含む)が年齢、職業、資産、収入からみて<u><b>過大</b></u>ではないか。"]}),t(s,{questionNumber:29,title:e,subject:n,contents:["保険加入のみを目的として設立された<u><b>団体でない</b></u>ことが契約の要件の1つである。","正しい","正しい"]}),t(s,{questionNumber:30,title:e,subject:n,contents:["復活とは、既に成立した契約が、保険料不払いのまま所定の期間（いわゆる<u><b>猶予期間</b></u>）を経過すれば契約は失効するが、一定の要件のもとに契約者が復活の請求をすれば、保険契約を有効状態に戻す制度である。","正しい","正しい"]}),t(c,{questionNumber:31,title:e,subject:n,items:[{label:"解",content:"死亡率は、死亡の状況を示す指標としてよく用いられるが、死亡の状況は年齢によって大きく異なるので、母集団の年齢構成が異なる場合には、死亡率だけで死亡の状況を比較することはできない。"},{label:"解",content:"金額死亡率のほうが件数死亡率よりも事業経営上に与える影響を反映しやすいので、生命保険経営上は、通常、件数死亡率よりも、金額死亡率が多く用いられる。"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"契約確認は<u><b>一部</b></u>の契約について契約成立前に行い，<u><b>大部分</b></u>は契約成立後に行っている。"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"解",content:"正しい"},{label:"ア",content:"選択表"},{label:"イ",content:"純保険料"},{label:"イ",content:"倫理"},{label:"オ",content:""},{label:"ウ",content:"22"},{label:"エ",content:"再診"},{label:"ウ",content:"不実の告知"},{label:"ア",content:"平均給付日数"},{label:"エ",content:"両眼の視力"},{label:"オ",content:""}]})]),t(l,{"previous-route":"kiken2024b","next-route":"kiken2023a"})])]),_:1}))}};export{h as default};
